import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import TableContainer from "@mui/material/TableContainer";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableBody from "@mui/material/TableBody";
import TableRow from "@mui/material/TableRow";
import TableCell from "@mui/material/TableCell";
import Paper from "@mui/material/Paper";
import { styled } from "@mui/material/styles";

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

function Pets() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [pets, setPets] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [formData, setFormData] = useState({
    animal: "",
    description: "",
    age: "",
    price: "",
  });
  const [editingPetId, setEditingPetId] = useState(null);

  useEffect(() => {
    fetchPets();
  }, []);

  const fetchPets = () => {
    fetch("http://localhost:3001/api?act=getall")
      .then((res) => res.json())
      .then((result) => {
        setIsLoaded(true);
        setPets(result);
      });
  };

  const addPet = () => {
    fetch(
      `http://localhost:3001/api?act=add&animal=${formData.animal}&description=${formData.description}&age=${formData.age}&price=${formData.price}`
    )
      .then((res) => res.json())
      .then(() => {
        fetchPets();
        setFormData({
          animal: "",
          description: "",
          age: "",
          price: "",
        });
      });
  };

  const deletePet = (id) => {
    fetch(`http://localhost:3001/api?act=delete&id=${id}`)
      .then((res) => res.json())
      .then(() => {
        fetchPets();
      });
  };

  const updatePet = (id, newData) => {
    fetch(
      `http://localhost:3001/api?act=update&id=${id}&animal=${newData.animal}&description=${newData.description}&age=${newData.age}&price=${newData.price}`
    )
      .then((res) => res.json())
      .then(() => {
        fetchPets();
      });
  };

  const handleInputChange = (e, id) => {
    const { name, value } = e.target;
    setPets((prevPets) =>
      prevPets.map((pet) => (pet.id === id ? { ...pet, [name]: value } : pet))
    );
  };

  const handleEdit = (id) => {
    setEditingPetId(id);
  };

  const handleSave = (id) => {
    const updatedPet = pets.find((pet) => pet.id === id);
    updatePet(id, updatedPet);
    setEditingPetId(null);
  };

  const handleCancel = () => {
    setEditingPetId(null);
  };

  return (
    <div>
      <h1>Server API Usage Demonstration</h1>
      <h2>Pets</h2>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <StyledTableCell>Animal</StyledTableCell>
              <StyledTableCell>Description</StyledTableCell>
              <StyledTableCell>Age</StyledTableCell>
              <StyledTableCell>Price</StyledTableCell>
              <StyledTableCell>Action</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {pets.map((pet) => (
              <StyledTableRow key={pet.id}>
                <StyledTableCell>
                  {editingPetId === pet.id ? (
                    <TextField
                      name="animal"
                      value={pet.animal}
                      onChange={(e) => handleInputChange(e, pet.id)}
                    />
                  ) : (
                    pet.animal
                  )}
                </StyledTableCell>
                <StyledTableCell>
                  {editingPetId === pet.id ? (
                    <TextField
                      name="description"
                      value={pet.description}
                      onChange={(e) => handleInputChange(e, pet.id)}
                    />
                  ) : (
                    pet.description
                  )}
                </StyledTableCell>
                <StyledTableCell>
                  {editingPetId === pet.id ? (
                    <TextField
                      name="age"
                      value={pet.age}
                      onChange={(e) => handleInputChange(e, pet.id)}
                    />
                  ) : (
                    pet.age
                  )}
                </StyledTableCell>
                <StyledTableCell>
                  {editingPetId === pet.id ? (
                    <TextField
                      name="price"
                      value={pet.price}
                      onChange={(e) => handleInputChange(e, pet.id)}
                    />
                  ) : (
                    pet.price
                  )}
                </StyledTableCell>
                <StyledTableCell>
                  {editingPetId === pet.id ? (
                    <>
                      <Button
                        variant="contained"
                        onClick={() => handleSave(pet.id)}
                      >
                        Save
                      </Button>
                      <Button variant="contained" onClick={handleCancel}>
                        Cancel
                      </Button>
                    </>
                  ) : (
                    <Button
                      variant="contained"
                      onClick={() => handleEdit(pet.id)}
                    >
                      Edit
                    </Button>
                  )}
                  <Button variant="contained" onClick={() => deletePet(pet.id)}>
                    Delete
                  </Button>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      <br />
      <h2>Add Pet</h2>
      <TextField
        label="Animal"
        name="animal"
        value={formData.animal}
        onChange={(e) => setFormData({ ...formData, animal: e.target.value })}
      />
      <TextField
        label="Description"
        name="description"
        value={formData.description}
        onChange={(e) =>
          setFormData({ ...formData, description: e.target.value })
        }
      />
      <TextField
        label="Age"
        name="age"
        value={formData.age}
        onChange={(e) => setFormData({ ...formData, age: e.target.value })}
      />
      <TextField
        label="Price"
        name="price"
        value={formData.price}
        onChange={(e) => setFormData({ ...formData, price: e.target.value })}
      />
      <Button variant="contained" onClick={addPet}>
        Add Pet
      </Button>
    </div>
  );
}

export default Pets;
