import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";

function Pets() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [pets, setPets] = useState([]);
  const [newPet, setNewPet] = useState({
    animal: "",
    description: "",
    age: "",
    price: "",
  });

  useEffect(() => {
    fetchPets();
  }, []);

  const fetchPets = () => {
    fetch("http://localhost:3001/api?act=getall")
      .then((res) => res.json())
      .then((result) => {
        setIsLoaded(true);
        setPets(result);
      });
  };

  const addPet = () => {
    fetch(
      "http://localhost:3001/api?act=add&animal=Dog&description=Dalmatian&age=3&price=103.45",
      {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(newPet),
      }
    )
      .then((res) => res.json())
      .then((result) => {
        fetchPets();
        setNewPet({ animal: "", description: "", age: "", price: "" }); // Clear form fields after adding a new pet
      });
  };

  const deletePet = (id) => {
    fetch(`http://localhost:3001/api?act=delete&id={id}`)
      .then((res) => res.json())
      .then((result) => {
        fetchPets();
      });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewPet({ ...newPet, [name]: value });
  };

  if (!isLoaded) {
    return <div>Loading...</div>;
  } else {
    return (
      <div>
        <h1>Pet Inventory</h1>
        <table>
          <thead>
            <tr>
              <th>Animal</th>
              <th>Description</th>
              <th>Age</th>
              <th>Price</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {pets.map((pet) => (
              <tr key={pet.id}>
                <td>{pet.animal}</td>
                <td>{pet.description}</td>
                <td>{pet.age}</td>
                <td>{pet.price}</td>
                <td>
                  <Button onClick={() => deletePet(pet.id)}>Delete</Button>
                </td>
              </tr>
            ))}
            <tr>
              <td>
                <input
                  type="text"
                  name="animal"
                  value={newPet.animal}
                  onChange={handleChange}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="description"
                  value={newPet.description}
                  onChange={handleChange}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="age"
                  value={newPet.age}
                  onChange={handleChange}
                />
              </td>
              <td>
                <input
                  type="text"
                  name="price"
                  value={newPet.price}
                  onChange={handleChange}
                />
              </td>
              <td>
                <Button onClick={addPet}>Add</Button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    );
  }
}

export default Pets;
